import React from 'react';

const SearchBox = ({searchField, searchChange}) =>{ //destructuring
  return (
    <div className='pa2'>
      <input className='pa2 ba b--green bg-lightest-blue'
       type='search' placeholder="Search robot" 
       onChange={searchChange} /> 
       {/* //onChange is an eventListener. */}
    </div>
  );
}

export default SearchBox;
