import React from 'react';
// import { robots } from './robots';  commented bc now using API, jsonplaceholder
import CardList from '../Components/CardList';
import SearchBox from '../Components/SearchBox';
// import Card from './card';
import './app.css';

// Ab hum ye data file se lene ki bajaaye ek API se uthaynge, because that's how 
// websites in real life work!! Aur wo website hai https://jsonplaceholder.typicode.com/users
import Scroll from '../Components/Scroll';
// const App = () => {
//   return(
//     <div className='tc'>
//       <h1>Robo Friends</h1>
//       <SearchBox />
//       <CardList robots={robots} />
//     </div>
//   );
// }

//Now the responsive search bar in making
class App extends React.Component {
  constructor(){
    super() //ES6 
    this.state = {
      // robots : robots  $isko ab empty array pass karaynge, bc using api now
      robots : [],
      searchField : ''
    }
  }

  //fetch is a window funciton, go to console and write windows.fetch
  //setState = set the state to 
  //syntax :
  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(response=> response.json())
    .then(users=> {this.setState({robots: users})});
  }

  onSearchChange = (event) => {
    //ye wo particular robot dega jiska naam type karenge
    this.setState({searchField: event.target.value})
  }

  render(){
    //destructuring this.state
    const {robots, searchField} = this.state;
    //a function whcih will give all the robots names from robot array
    const filteredRobs = robots.filter(robot =>{
      return robot.name.toLowerCase().includes(searchField.toLowerCase());
    })

    return !robots.length ? 
      <h1>Loading...</h1> : 
      (
        <div className='tc'>
          <h1 className='f2'>RoboFriends</h1>
          {/* searchChagne is a prop */}
            <SearchBox searchChange = {this.onSearchChange} />
          <Scroll>
            <CardList robots = {filteredRobs} />
          </Scroll>
        </div>
      );
  }
}

export default App;
