//       <h1>Robo Friends</h1>
//       <SearchBox />
//       <CardList robots={robots} />
//     </div>
//   );
// }

//Now the responsive search bar in making
class App extends React.Component {
  constructor(){
    super() //ES6 
    this.state = {
      robots : robots,
      searchField : ''
    }
  }

  onSearchChange = (event) => {
    //ye wo particular robot dega jiska naam type karenge
    this.setState({searchField: event.target.value})
  }

  render(){
    //a function whcih will give all the robots names from robot array
    const filteredRobs = this.state.robots.filter(robots =>{
      return robots.name.toLowerCase().includes(this.state.searchField.toLowerCase());
    })
    
    return (
      <div className='tc'>
        <h1>RoboFriends</h1>
        {/* searchChagne is a prop */}
        <SearchBox searchChange = {this.onSearchChange} />
        <CardList robots = {filteredRobs} />
      </div>
    );
  }
}

export default App;
