import React from 'react';
import Card from './card';

const CardList = ( {robots} ) => {
  const cardComp = robots.map((user, i) =>{
    return <Card key= {i}
            id={robots[i].id} 
            name={robots[i].name} 
            email={robots[i].email} />
  });
  return (
    <div>
      {cardComp}
    </div>
  );
}

