import React from 'react';

const Card = ({ name, email, id }) => {    //prev was = const Card = (props)
//  const {name, email , id} = props;  called as destructuring.
  return(
    <div className ='tc grow bg-light-green br3 pa2 ma2 dib bw2 shadow-5'>
      <img alt='robot' src={`https://robohash.org/${id}?200x200`} />
      <div>
        {/* wrap it in {} because it's JSX and in JSX, js is written in {} */}
        <h2>{name}</h2> 
        <p>{email}</p>
      </div>
    </div>
  );
}

export default Card;
